import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import RSVPButton from '../components/Events/RSVPButton';
import AttendeeList from '../components/Events/AttendeeList';
import { useAuth } from '../context/AuthProvider';

import './EventPage.css';

const EventPage = () => {
  const { id: eventId } = useParams();
  const { user, loading } = useAuth();
  const [event, setEvent] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You must be logged in to view event details.');
      return;
    }

    axios.get(`/api/events/${eventId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setEvent(res.data))
    .catch(err => {
      console.error('Failed to load event:', err);
      setError('Failed to load event. Please try again.');
    });
  }, [eventId]);

  if (loading) return <p>Loading user info…</p>;
  if (error) return <p>{error}</p>;
  if (!event) return <p>Loading event…</p>;
  if (!user) return <p>Please login to view event details.</p>;

  const isOrganizer = ['organizer', 'clubadmin', 'admin'].includes(user.role);

  return (
    <div className="container mt-4">
      <h1>{event.title}</h1>
      <p>{event.description}</p>
      <p>
        <strong>Date: </strong>
        {new Date(event.date).toLocaleString()}
      </p>

      <RSVPButton eventId={eventId} currentUser={user} />

      {isOrganizer && (
        <div className="mt-5">
          <AttendeeList eventId={eventId} />
        </div>
      )}
    </div>
  );
};

export default EventPage;
