import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import './UserDashboard.css'; // Reuse styles

function ClubDashboard() {
  const navigate = useNavigate();

  return (
    <>
      <Header />
      <div className="dashboard-container">
        <h1>Club Dashboard</h1>

        {/* Button to navigate to the dedicated event management page */}
        <button
          onClick={() => navigate('/club-dashboard/events')}
          className="btn-manage-events"
          style={{
            marginBottom: '20px',
            padding: '10px 20px',
            backgroundColor: '#1976d2',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '1rem'
          }}
        >
          Manage Events
        </button>

        {/* Poll management button */}
        <button
          onClick={() => navigate('/create-poll')}
          className="btn-create-poll"
          style={{
            marginBottom: '20px',
            padding: '10px 20px',
            backgroundColor: '#0059cc',
            color: 'white',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontWeight: '600',
            fontSize: '1rem'
          }}
        >
          + Create Poll
        </button>

        {/* Other club management sections can go here */}
      </div>
    </>
  );
}

export default ClubDashboard;
