import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import useAuth from '../hooks/useAuth';
import './AnnouncementsPage.css'; // Adapt or add your CSS here

const ANNOUNCEMENTS_API = 'http://localhost:5000/api/announcements';

const AnnouncementsPage = () => {
  const { clubId } = useParams();
  const { user } = useAuth();
  const [selectedClub, setSelectedClub] = useState(null);
  const [announcements, setAnnouncements] = useState([]);
  const [newAnnouncement, setNewAnnouncement] = useState({ title: '', content: '' });
  const [loading, setLoading] = useState(true);
  const [posting, setPosting] = useState(false);

  // Fetch club details to get members and roles (adjust API path if different)
  useEffect(() => {
    const fetchClub = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5000/api/clubs/${clubId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSelectedClub(res.data);
      } catch (err) {
        console.error('Failed to fetch club:', err);
      }
    };
    if (clubId) fetchClub();
  }, [clubId]);

  // Fetch announcements
  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${ANNOUNCEMENTS_API}/${clubId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setAnnouncements(res.data);
      } catch (err) {
        console.error('Failed to fetch announcements:', err);
      } finally {
        setLoading(false);
      }
    };
    if (clubId) fetchAnnouncements();
  }, [clubId]);

  // Determine if current user is officer/admin for role-based UI control
  const isOfficer = selectedClub?.members?.some(m => {
  if (!m.userId) return false;
  const memberUserId = typeof m.userId === 'string' ? m.userId : m.userId._id;
  return memberUserId === user._id && ['officer', 'club_admin'].includes(m.role);
});



  const handleCreateAnnouncement = async () => {
    if (!newAnnouncement.title.trim() || !newAnnouncement.content.trim()) return;
    setPosting(true);
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${ANNOUNCEMENTS_API}/${clubId}`,
        newAnnouncement,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAnnouncements(res.data.announcements);
      setNewAnnouncement({ title: '', content: '' });
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to post announcement');
      console.error(err);
    }
    setPosting(false);
  };

  const handleDeleteAnnouncement = async (annId) => {
    if (!window.confirm('Are you sure you want to delete this announcement?')) return;
    try {
      const token = localStorage.getItem('token');
      const res = await axios.delete(
        `${ANNOUNCEMENTS_API}/${clubId}/${annId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAnnouncements(res.data.announcements);
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to delete announcement');
      console.error(err);
    }
  };

  if (loading) return <p>Loading announcements...</p>;

  return (
    <div className="announcements-page-container">
      <h1>Announcements for {selectedClub?.name || 'Club'}</h1>

      {isOfficer && (
        <div className="announcement-form">
          <input
            type="text"
            placeholder="Announcement title"
            value={newAnnouncement.title}
            onChange={(e) => setNewAnnouncement({ ...newAnnouncement, title: e.target.value })}
          />
          <textarea
            placeholder="Write announcement..."
            value={newAnnouncement.content}
            onChange={(e) => setNewAnnouncement({ ...newAnnouncement, content: e.target.value })}
          />
          <button onClick={handleCreateAnnouncement} disabled={posting}>
            {posting ? 'Posting...' : 'Post Announcement'}
          </button>
        </div>
      )}

      {announcements.length === 0 ? (
        <p>No announcements yet.</p>
      ) : (
        announcements.map((a) => (
          <div key={a._id} className="announcement-item">
            <h3>{a.title}</h3>
            <p>{a.content}</p>
            <small>
              Posted by {a.author?.name || 'Unknown'} on {new Date(a.createdAt).toLocaleString()}
            </small>
            {isOfficer && (
              <button className="delete-btn" onClick={() => handleDeleteAnnouncement(a._id)}>
                Delete
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default AnnouncementsPage;
