import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import RSVPButton from '../components/Events/RSVPButton';
import AttendeeList from '../components/Events/AttendeeList';
import useAuth from '../hooks/useAuth';

import './EventPage.css';

const EventPage = () => {
  const { id: eventId } = useParams();
  const { user } = useAuth();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
console.log('User:', user);
console.log('Is organizer:', isOrganizer);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('You must be logged in to view event details.');
      setLoading(false);
      return;
    }

    axios.get(`/api/events/${eventId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setEvent(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Failed to load event:', err);
        setError('Failed to load event. Please try again.');
        setLoading(false);
      });
  }, [eventId]);

  if (loading) return <p>Loading event…</p>;
  if (error) return <p>{error}</p>;

  // Adjust role check to match your system's organizer/club admin roles
  const isOrganizer = user && ['organizer', 'clubadmin', 'admin'].includes(user.role);


  return (
    <div className="container mt-4">
      <h1>{event.title}</h1>
      <p>{event.description}</p>
      <p>
        <strong>Date: </strong>
        {new Date(event.date).toLocaleString()}
      </p>

      <RSVPButton eventId={eventId} currentUser={user} />

      {isOrganizer && (
        <div className="mt-5">
          <AttendeeList eventId={eventId} />
        </div>
      )}
    </div>
  );
};

export default EventPage;
