import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import axios from 'axios';
import './EventCalendar.css'; 

const EventCalendar = () => {
  const [events, setEvents] = useState([]);
  const [filters, setFilters] = useState({
    category: '',
    clubId: '',
    startDate: '',
    endDate: ''
  });



  // Fetch clubs for filter dropdown (optional)
  const [clubs, setClubs] = useState([]);
  useEffect(() => {
    axios.get('/api/clubs')
      .then(res => setClubs(res.data))
      .catch(err => {});
  }, []);

  // Fetch events with filters
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const res = await axios.get('/api/events/filter', { params: filters });
        // Transform for FullCalendar
        setEvents(res.data.map(e => ({
          id: e._id,
          title: e.title,
          start: e.date,
          display: 'block',
          backgroundColor: '#3788d8',
          borderColor: '#3788d8',
          extendedProps: {
            location: e.location,
            category: e.category,
            club: e.clubId?.name
          }
        })));
      } catch (err) {/*...*/}
    };
    fetchEvents();
  }, [filters]);

  const handleEventClick = (info) => {
    const { title, extendedProps } = info.event;
    alert(
      `Event: ${title}\nLocation: ${extendedProps.location}\nCategory: ${extendedProps.category || ''}\nClub: ${extendedProps.club || ''}`
    );
  };

  return (
    <div className="calendar-page">
      <h2 className="calendar-title">Event Calendar</h2>
      <div className="calendar-filters">
        <select value={filters.category} onChange={e => setFilters({ ...filters, category: e.target.value })}>
          <option value="">All Categories</option>
          <option>Academic</option>
          <option>Sports</option>
          <option>Cultural</option>
          <option>Tech</option>
          <option>Social</option>
        </select>
        <select value={filters.clubId} onChange={e => setFilters({ ...filters, clubId: e.target.value })}>
          <option value="">All Clubs</option>
          {clubs.map(club => <option key={club._id} value={club._id}>{club.name}</option>)}
        </select>
        <input
          type="date"
          value={filters.startDate}
          onChange={e => setFilters({ ...filters, startDate: e.target.value })}
          placeholder="Start Date"
        />
        <input
          type="date"
          value={filters.endDate}
          onChange={e => setFilters({ ...filters, endDate: e.target.value })}
          placeholder="End Date"
        />
        <button onClick={() => setFilters({ category: '', clubId: '', startDate: '', endDate: '' })}>
          Clear
        </button>
      </div>

      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        events={events}
        eventClick={handleEventClick}
        height="auto"
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth'
        }}
      />
    </div>
  );
};

export default EventCalendar;
